import React, { useState, useEffect } from 'react';

import axios from '../../utils/axios';
import requests from '../../utils/requests';

import './SearchResult.css';

const base_url = 'https://image.tmdb.org/t/p/original';

const SearchResult = ({query}) => {
	const [movies, setMovies] = useState([]);
	// const url = `${requests.fetchSearch}/${String(query.keywords)}?genre=${parseInt(query.genre)}&mediaType=${query.mediaType}&language=${query.language}&year=${query.year}`;
	// if(query.genre!='Genre' && query.genre!=''){

	// }
	const checkurl = (query) => {
		const newurl = `${requests.fetchSearch}/${String(query.keywords)}`;
		if(query.genre!='Genre' && query.genre!='')
		{
			newurl+=`?genre=${parseInt(query.genre)}`
		}
		console.log(newurl)

	}
	checkurl(query)


	useEffect(() => {
		async function fetchData() {
			const request = await axios.get();
			setMovies(request.data.results);
			return request;
		}

		if (query) {
			fetchData();
		} else {
			setMovies([]);
		}
	}, [, query]);

	return(
		<div className='row'>
			<h2>Search Result</h2>
			<div className='row_posters search-resul-container sc2'>
					{movies.map((movie) => {
						return (
							<img
								key={movie.id}
								className={`row_poster row_posterLarge`}
								src={`${base_url}${movie.poster_path}`}
								alt={movie.name}
							/>
						);
					})}
			</div>
		</div>
	)
};

export default SearchResult;
